package com.ufgov.zc.client.component;import java.awt.FlowLayout;import java.awt.GridBagConstraints;import java.awt.GridBagLayout;import java.awt.Insets;import javax.swing.JLabel;import javax.swing.JPanel;import javax.swing.JTextField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.Contract;public class ContractSelectPanel extends JPanel {  private JLabel contractNoLbl = new JLabel("    合同号:");  private ContractSelectField contractNoTxt = new ContractSelectField(20);  private JLabel contractNameLbl = new JLabel("合同名称:");  private JTextField contractNameTxt = new JTextField();  public ContractSelectPanel() {    this.setLayout(new GridBagLayout());    JPanel pn3 = new JPanel(new FlowLayout(FlowLayout.LEFT));    pn3.add(contractNoLbl);    pn3.add(contractNoTxt);    contractNoTxt.addValueChangeListener(new ValueChangeListener() {      public void valueChanged(ValueChangeEvent e) {        Contract contract = contractNoTxt.getContract();        if (contract != null) {          String name = contract.getContractName();          if (name != null)            contractNameTxt.setText(name);          else            contractNameTxt.setText("");        }      }    });    JPanel pn4 = new JPanel(new FlowLayout(FlowLayout.LEFT));    pn4.add(contractNameLbl);    contractNameTxt.setColumns(20);    contractNameTxt.setEditable(false);    pn4.add(contractNameTxt);    this.add(pn3, new GridBagConstraints(0, 0, 1, 1, 0.1, 0.0,    GridBagConstraints.WEST, GridBagConstraints.HORIZONTAL, new Insets(2, 2, 2, 2), 0,    0));    this.add(pn4, new GridBagConstraints(1, 0, 1, 1, 0.7, 0.0,    GridBagConstraints.WEST, GridBagConstraints.HORIZONTAL, new Insets(2, 2, 2, 2), 0,    0));  }  public void setContractCode(String value) {    contractNoTxt.setText(value);  }  public String getContractCode() {    return contractNoTxt.getText();  }}