package com.ufgov.zc.client.component;import java.awt.BorderLayout;import java.awt.Dialog;import java.awt.event.WindowAdapter;import java.awt.event.WindowEvent;import javax.swing.JFrame;import com.ufgov.zc.common.commonbiz.model.NumLimCompo;public class NumLimCompoTreeSelectField extends JButtonTextField {  public NumLimCompoTreeSelectField() {    super();    this.init();  }  public NumLimCompoTreeSelectField(int len) {    super(len);    this.init();  }  public NumLimCompoTreeSelectField(int len, Dialog owner) {    super(len);    this.owner = owner;    this.init();  }  private void init() {    selectDialog = new NumLimCompoTreeSelectDialog(owner, true, this);  }  public NumLimCompo getNumLimCompo() {    return (NumLimCompo) this.getValue();  }  public void handleClick(JButtonTextField jButtonTextField) {    selectDialog.setVisible(true);  }  public static void main(String[] args) {    NumLimCompoTreeSelectField selField = new NumLimCompoTreeSelectField(20);    JFrame f = new JFrame();    f.getContentPane().setLayout(new BorderLayout());    f.getContentPane().add(selField, BorderLayout.NORTH);    f.setSize(400, 300);    f.setLocationRelativeTo(null);    f.setVisible(true);    f.addWindowListener(new WindowAdapter() {      public void windowClosing(WindowEvent e) {        System.exit(0);      }    });  }}