package com.ufgov.zc.client.component;import java.awt.BorderLayout;import java.awt.event.WindowAdapter;import java.awt.event.WindowEvent;import javax.swing.JComboBox;import javax.swing.JFrame;import javax.swing.JPanel;import com.ufgov.smartclient.component.JComboBoxEx;import com.ufgov.zc.client.component.ui.fieldeditor.SeasonComboFieldEditor;public class SeasonComboBox extends JComboBoxEx {  private JComboBox comboBo = new JComboBox();  private boolean firstAddNull = false;  private String[] dataInfo = new String[] { "第一季度", "第二季度", "第三季度", "第四季度" };  public SeasonComboBox() {    setItems(dataInfo);  }  public SeasonComboBox(boolean firstAddNull) {    this.firstAddNull = firstAddNull;    setItems(dataInfo);  }  public SeasonComboBox(Object[] items) {    setItems(items);  }  public void setItems(Object[] items) {    this.removeAllItems();    if (firstAddNull) {      this.addItem(null);    }    for (int i = 0; i < items.length; i++) {      this.addItem(items[i]);    }  }  public static void main(String[] args) {    JFrame f = new JFrame();    SeasonComboFieldEditor textField = new SeasonComboFieldEditor("季度");    JPanel panel = new JPanel();    panel.add(textField);    f.getContentPane().add(panel, BorderLayout.NORTH);    f.setSize(400, 300);    f.setLocationRelativeTo(null);    f.setVisible(true);    f.addWindowListener(new WindowAdapter() {      public void windowClosing(WindowEvent e) {        System.exit(0);      }    });  }}