package com.ufgov.zc.client.component;import javax.swing.RowFilter;public class SimpleRowFilter extends RowFilter {  String filterText = "";  public SimpleRowFilter(String filterText) {    this.filterText = filterText;  }  public boolean include(Entry entry) {    int count = entry.getValueCount();        String[] textArray = filterText.split(" ");        boolean isInclude = true;        for(int j=0; j<textArray.length; j++){    	    	boolean isRow = false;    	    	for (int i = 0; i < count; i++) {    		    		if (textArray[j].trim().length()>0 && entry.getStringValue(i).indexOf(textArray[j].trim()) < 0) {    			    			isRow = false;    			    		} else {    			isRow = true;    			    			break;    		}    		    	}    	    	isInclude = isInclude&isRow;    	    }    return isInclude;  }}