package com.ufgov.zc.client.component.element;import java.util.ArrayList;import java.util.List;import java.util.Map;import com.ufgov.zc.client.component.table.BeanTableModel;import com.ufgov.zc.client.component.table.ColumnBeanPropertyPair;import com.ufgov.zc.common.commonbiz.model.Fund;import com.ufgov.zc.common.commonbiz.model.Manage;import com.ufgov.zc.common.system.constants.BiElementConstants;public class FundForBiXJTableModel extends BeanTableModel<Fund> {  private static final long serialVersionUID = -3683271040414699401L;  public FundForBiXJTableModel(List<Fund> pDataList) {    refreshData(pDataList);  }  public FundForBiXJTableModel(List<Fund> pDataList, boolean editable) {    this.editable = editable;    refreshData(pDataList);  }  public synchronized void refreshData(List<Fund> list) {    this.setDataBean(list, createColumnBeanPropertyPairs());  }  private List<ColumnBeanPropertyPair> createColumnBeanPropertyPairs() {    List<ColumnBeanPropertyPair> pairList = new ArrayList<ColumnBeanPropertyPair>();    ColumnBeanPropertyPair pair = new ColumnBeanPropertyPair();    pair.setColumnIdentifier("资金属性代码");    pair.setBeanPropertyName("code");    pairList.add(pair);    pair = new ColumnBeanPropertyPair();    pair.setColumnIdentifier("年度");    pair.setBeanPropertyName("nd");    pairList.add(pair);    pair = new ColumnBeanPropertyPair();    pair.setColumnIdentifier("资金属性");    pair.setBeanPropertyName("name");    pairList.add(pair);    return pairList;  }  public void setValueAt(Fund fund, int row) {    if (fund == null) {      return;    }    if (row >= 0 && row < this.getRowCount()) {      for (int col = 0; col < this.getBeanPropertyNames().size(); col++) {        super.setValueAt(fund.get(this.getBeanPropertyNames().get(col)), row, col);      }    }  }  public void setValueAt(Object aValue, int rowIndex, int columnIndex) {    super.setValueAt(aValue, rowIndex, columnIndex);  }  public boolean isCellEditable(int row, int column) {    if (this.editable) {      if (getBeanPropertyNames().get(column).equals("nd")) {        return false;      }      return true;    } else {      return false;    }  }  protected void putBeanToMap(Fund fund, Map<Object, Fund> dataMap) {    if (fund == null) {      return;    }    dataMap.put(fund.getOid(), fund);  }  public List<Fund> getDataList() {    return this.getDataBeanList();  }}