package com.ufgov.zc.client.component.element;import java.awt.Dialog;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;import javax.swing.JOptionPane;import javax.swing.tree.DefaultMutableTreeNode;import javax.swing.tree.DefaultTreeModel;import com.ufgov.zc.client.common.LangTransMeta;import com.ufgov.zc.client.common.ServiceFactory;import com.ufgov.zc.client.common.WorkEnv;import com.ufgov.zc.client.component.JButtonTextField;import com.ufgov.zc.client.component.JTreeSelectDialog;import com.ufgov.zc.client.datacache.FundDataCache;import com.ufgov.zc.client.util.NumLimUtil;import com.ufgov.zc.common.commonbiz.model.Fund;import com.ufgov.zc.common.commonbiz.publish.IBaseDataServiceDelegate;import com.ufgov.zc.common.system.RequestMeta;import com.ufgov.zc.common.system.constants.NumLimConstants;import com.ufgov.zc.common.system.dto.ElementConditionDto;public class FundTreeSelectDialog extends JTreeSelectDialog {  public FundTreeSelectDialog(Dialog owner, boolean modal,  JButtonTextField triggerField) {    super(owner, modal, triggerField);  }  protected void initTitle() {    LangTransMeta.init("GK%");    this.setTitle(LangTransMeta.translate("GK_TITLE_FUND"));  }  protected void initDataBufferList() {    int nd = WorkEnv.getInstance().getTransNd();    IBaseDataServiceDelegate baseDataServiceDelegate = (IBaseDataServiceDelegate) ServiceFactory    .create(IBaseDataServiceDelegate.class, "baseDataServiceDelegate");    RequestMeta requestMeta = WorkEnv.getInstance().getRequestMeta();    ElementConditionDto dto = new ElementConditionDto();    dto.setNd(nd);    dataBufferList = FundDataCache.getFund();    dto.setNumLimitStr(    NumLimUtil.getInstance().getNumLimCondByCoType(triggerField.compoId, NumLimConstants.FWATCH, triggerField.elementCode));    if (dto.getNumLimitStr() == null) {      numLimDataList = dataBufferList;    } else {      numLimDataList = baseDataServiceDelegate.getFund(dto, requestMeta);    }    for (int i = 0; i < dataBufferList.size(); i++) {      Fund rowData = (Fund) dataBufferList.get(i);      this.triggerField.dataMap.put(rowData.getCode(), rowData);    }    this.triggerField.filteredDataList = dataBufferList;  }  private List genTreeData() {    List filteredList = genFilteredDataList();    Map map = new HashMap();    for (Object o : filteredList) {      Fund temp = (Fund) o;      map.put(temp.getCode(), temp);    }    List rootFundList = new ArrayList();    List childrenFundList = new ArrayList();    for (Object o : filteredList) {      Fund temp = (Fund) o;      if (map.get(temp.getParentCode()) == null) {        rootFundList.add(temp);      } else {        childrenFundList.add(temp);      }    }    Map childrenMap = new HashMap();    for (int i = 0; i < childrenFundList.size(); i++) {      Fund child = (Fund) childrenFundList.get(i);      List childrenList = (List) childrenMap.get(child.getParentCode());      if (childrenList != null) {        childrenList.add(child);      } else {        List tempList = new ArrayList();        tempList.add(child);        childrenMap.put(child.getParentCode(), tempList);      }    }    for (int i = 0; i < rootFundList.size(); i++) {      Fund root = (Fund) rootFundList.get(i);      this.setFundChildren(root, childrenMap);    }    return rootFundList;  }  private List genFilteredDataList() {    List prefixFilteredFundList = new ArrayList();    List levelCtrlFilteredFundList = new ArrayList();    if (!this.triggerField.isRandomEdit()) {      if (this.triggerField.getPrefix() == null) {        this.triggerField.setPrefix("");      }      for (Object o : dataBufferList) {        Fund temp = (Fund) o;        if (temp.getCode().startsWith(this.triggerField.getPrefix())) {          prefixFilteredFundList.add(temp);        }      }    }    if (this.triggerField.isLevelCtrl()) {      for (Object o : dataBufferList) {        Fund temp = (Fund) o;        if (temp.getCode().length() <= this.triggerField.getCtrlLen()) {          levelCtrlFilteredFundList.add(temp);        }      }    }    List filteredList = null;    if (this.triggerField.isRandomEdit() && !this.triggerField.isLevelCtrl()) {      filteredList = this.dataBufferList;    } else if (!this.triggerField.isRandomEdit() && !this.triggerField.isLevelCtrl()) {      filteredList = prefixFilteredFundList;    } else if (this.triggerField.isRandomEdit() && this.triggerField.isLevelCtrl()) {      filteredList = levelCtrlFilteredFundList;    } else {      filteredList = new ArrayList();      for (Object o : prefixFilteredFundList) {        if (levelCtrlFilteredFundList.contains(o)) {          filteredList.add(o);        }      }    }    return filteredList;  }  private void setFundChildren(Fund v, Map childrenMap) {    List childrenList = (List) childrenMap.get(v.getCode());    if (childrenList != null) {      v.setChildren(childrenList);      for (int i = 0; i < childrenList.size(); i++) {        Fund c = (Fund) childrenList.get(i);        setFundChildren(c, childrenMap);      }    }  }  protected void initSelectTree() {    DefaultMutableTreeNode root = new DefaultMutableTreeNode("资金性质");    this.triggerField.filteredDataList = this.genFilteredDataList();    List bAccList = genTreeData();    treeNodeMap.clear();    for (Object o : bAccList) {      Fund v = (Fund) o;      if (isNumLimContain(v)) {        DefaultMutableTreeNode node = new DefaultMutableTreeNode(v);        root.add(node);        treeNodeMap.put(v, node);        this.setChildNode(v, node);      }    }    this.getSelectTree().setModel(new DefaultTreeModel(root));  }  private void setChildNode(Fund v, DefaultMutableTreeNode node) {    if (v.getChildren().size() > 0) {      for (Object o : v.getChildren()) {        Fund c = (Fund) o;        if (isNumLimContain(c)) {          DefaultMutableTreeNode childNode = new DefaultMutableTreeNode(c);          node.add(childNode);          treeNodeMap.put(c, childNode);          setChildNode(c, childNode);        }      }    }  }  public void doOK() {    if (this.triggerField.isLevelCtrl()) {      DefaultMutableTreeNode node = (DefaultMutableTreeNode) selectTree      .getLastSelectedPathComponent();      if (node == null) {        return;      }      if (!node.isLeaf()) {        JOptionPane.showMessageDialog(self, "只能选择叶子节点!", "提示",        JOptionPane.INFORMATION_MESSAGE);        return;      }    }    super.doOK();  }}