package com.ufgov.zc.client.component.element;import java.util.ArrayList;import java.util.List;import java.util.Map;import com.ufgov.zc.client.component.table.BeanTableModel;import com.ufgov.zc.client.component.table.ColumnBeanPropertyPair;import com.ufgov.zc.common.commonbiz.model.Manage;public class ManageForBiXJTableModel extends BeanTableModel<Manage> {  private static final long serialVersionUID = -3683271040414699401L;  public ManageForBiXJTableModel(List<Manage> pDataList) {    refreshData(pDataList);  }  public ManageForBiXJTableModel(List<Manage> pDataList, boolean editable) {    this.editable = editable;    refreshData(pDataList);  }  public synchronized void refreshData(List<Manage> list) {    this.setDataBean(list, createColumnBeanPropertyPairs());  }  private List<ColumnBeanPropertyPair> createColumnBeanPropertyPairs() {    List<ColumnBeanPropertyPair> pairList = new ArrayList<ColumnBeanPropertyPair>();    ColumnBeanPropertyPair pair = new ColumnBeanPropertyPair();    pair.setColumnIdentifier("资金类别代码");    pair.setBeanPropertyName("code");    pairList.add(pair);    pair = new ColumnBeanPropertyPair();    pair.setColumnIdentifier("年度");    pair.setBeanPropertyName("nd");    pairList.add(pair);    pair = new ColumnBeanPropertyPair();    pair.setColumnIdentifier("资金类别");    pair.setBeanPropertyName("name");    pairList.add(pair);    return pairList;  }  public void setValueAt(Manage manage, int row) {    if (manage == null) {      return;    }    if (row >= 0 && row < this.getRowCount()) {      for (int col = 0; col < this.getBeanPropertyNames().size(); col++) {        super.setValueAt(manage.get(this.getBeanPropertyNames().get(col)), row, col);      }    }  }  public void setValueAt(Object aValue, int rowIndex, int columnIndex) {    super.setValueAt(aValue, rowIndex, columnIndex);  }  public boolean isCellEditable(int row, int column) {    if (this.editable) {      if (getBeanPropertyNames().get(column).equals("nd")) {        return false;      }      return true;    } else {      return false;    }  }  protected void putBeanToMap(Manage manage, Map<Object, Manage> dataMap) {    if (manage == null) {      return;    }    dataMap.put(manage.getOid(), manage);  }  public List<Manage> getDataList() {    return this.getDataBeanList();  }}