package com.ufgov.zc.client.component.setting.cp;import javax.swing.JTextField;import com.ufgov.zc.client.component.IntDocument;import com.ufgov.zc.client.component.setting.TextFieldOption;import com.ufgov.zc.common.system.constants.BusinessOptionConstants;public class CpCheckNoLengthOption extends TextFieldOption {  public CpCheckNoLengthOption() {    this.init(BusinessOptionConstants.OPT_CP_CHECK_NO_LENGTH, "增加一个系统选项设置支票号的长度：");  }  protected JTextField createOptionEditor() {    JTextField field = super.createOptionEditor();    String v = field.getText();    field.setDocument(new IntDocument());    field.setText(v);    return field;  }}