package com.ufgov.zc.client.component.setting.dp;import com.ufgov.smartclient.component.JComboBoxEx;import com.ufgov.zc.client.common.AsOptionMeta;import com.ufgov.zc.client.component.setting.AbstractOption;import com.ufgov.zc.common.system.constants.BusinessOptionConstants;public class DpEditMonthOption extends AbstractOption<JComboBoxEx> {  /**   *    */  private static final long serialVersionUID = 1L;  public DpEditMonthOption() {    this.init(BusinessOptionConstants.OPT_DPEDIT_MONTH, "计划编制月份：");  }  protected JComboBoxEx createOptionEditor() {    String value = AsOptionMeta.getOptVal(optId);    this.oldValue = value;    final JComboBoxEx editor = new JComboBoxEx();    editor.addItem("0");    editor.addItemDisplaLable("0", "当月");    editor.addItem("1");    editor.addItemDisplaLable("1", "下月");    editor.setSelectedItem(value);    return editor;  }  public void updateOption() {    String v = (String) this.optionEditor.getSelectedItem();    if (!v.equals(oldValue)) {      AsOptionMeta.updateOptVal(optId, v);      oldValue = v;    }  }}