package com.ufgov.zc.client.component.setting.dp;import java.awt.Window;import java.util.ArrayList;import java.util.List;import com.ufgov.zc.client.component.setting.AbstractOption;import com.ufgov.zc.client.component.setting.OptionTab;public class DpOptionTab extends OptionTab {  private Window owner;  public DpOptionTab(Window owner) {    this.owner = owner;    List<AbstractOption> optionList = new ArrayList<AbstractOption>();    AbstractOption ao = new DpEditMonthOption();    optionList.add(ao);    ao = new RoundNumComboBoxOption();    optionList.add(ao);    this.init("systemOptionTab", "计划系统的系统选项", optionList);  }  @Override  protected void doCancel() {    owner.dispose();  }}