package com.ufgov.zc.client.component.setting.dp;import com.ufgov.smartclient.component.JComboBoxEx;import com.ufgov.zc.client.common.AsOptionMeta;import com.ufgov.zc.client.component.setting.AbstractOption;import com.ufgov.zc.common.system.constants.BusinessOptionConstants;public class RoundNumComboBoxOption extends AbstractOption<JComboBoxEx> {  /**   *    */  private static final long serialVersionUID = 1L;  public RoundNumComboBoxOption() {    this.init(BusinessOptionConstants.OPT_DP_EQUAL_SPLIT_ROUND_NUM, "均分取整位数：");  }  protected JComboBoxEx createOptionEditor() {    String value = AsOptionMeta.getOptVal(optId);    this.oldValue = value;    final JComboBoxEx editor = new JComboBoxEx();    editor.addItem("0");    editor.addItem(1 + "");    editor.addItem(10 + "");    editor.addItem(100 + "");    editor.addItem(1000 + "");    editor.addItem(10000 + "");    editor.addItemDisplaLable(0 + "", "");    editor.addItemDisplaLable(1 + "", "个位");    editor.addItemDisplaLable(10 + "", "十位");    editor.addItemDisplaLable(100 + "", "百位");    editor.addItemDisplaLable(1000 + "", "千位");    editor.addItemDisplaLable(10000 + "", "万位");    editor.setSelectedItem(value);    return editor;  }  public void updateOption() {    String size = (String) this.optionEditor.getSelectedItem();    if (!size.equals(oldValue)) {      AsOptionMeta.updateOptVal(optId, size);      oldValue = size;    }  }}