package com.ufgov.zc.client.component.table.celleditor.zc;import java.awt.Component;import javax.swing.AbstractCellEditor;import javax.swing.JTable;import javax.swing.table.TableCellEditor;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.client.component.zc.fieldeditor.emexperttype.EmExpertTypeSelectionTreeSelectField;import com.ufgov.zc.common.zc.model.EmExpertTypeSelection;public class ZcEmExpertTypeSelectionCellEditor extends AbstractCellEditor implements TableCellEditor {  private static final long serialVersionUID = -577377886496832154L;  private EmExpertTypeSelectionTreeSelectField selectField;  private ZcEmExpertTypeSelectionCellEditor self = this;  public ZcEmExpertTypeSelectionCellEditor() {    selectField = new EmExpertTypeSelectionTreeSelectField();    selectField.addValueChangeListener(new ValueChangeListener() {      public void valueChanged(ValueChangeEvent e) {        self.stopCellEditing();      }    });  }  public Component getTableCellEditorComponent(JTable table, Object value, boolean isSelected, int row,  int column) {    selectField.setValueByCode((String) value);    return selectField;  }  public Object getCellEditorValue() {    Object obj = selectField.getEmExpertTypeSelection();    if (obj != null) {      return selectField.getEmExpertTypeSelection();    } else {      return new EmExpertTypeSelection();    }  }  public EmExpertTypeSelectionTreeSelectField getEditor() {    return this.selectField;  }}