package com.ufgov.zc.client.component.table.cellrenderer;import java.awt.Component;import java.util.Date;import javax.swing.JTable;import com.ufgov.smartclient.component.table.TableCellDisplayValueProvider;import com.ufgov.zc.common.system.util.DateUtil;public class DateTime24CellRenderer extends LineWrapCellRenderer implements TableCellDisplayValueProvider {  private static final long serialVersionUID = 446230596940518406L;  public Component getTableCellRendererComponent(JTable table, Object value, boolean isSelected,  boolean hasFocus, int row, int column) {    String dateString = "";    if (value instanceof java.util.Date) {      dateString = DateUtil.dateToSsString((Date) value);    }    return super.getTableCellRendererComponent(table, dateString, isSelected, hasFocus, row, column);  }  @Override  public Object getDisplayValue(JTable jtable, Object obj, int i, int j) {    if (obj instanceof java.util.Date) {      return DateUtil.dateToSsString((Date) obj);    }    return "";  }}