package com.ufgov.zc.client.component.table.cellrenderer;import java.awt.Component;import java.text.NumberFormat;import javax.swing.JLabel;import javax.swing.JTable;import javax.swing.table.DefaultTableCellRenderer;public class IntCellRenderer extends DefaultTableCellRenderer {  /**   *    */  private static final long serialVersionUID = 5739234501200780350L;  private NumberFormat numberFormat;  public IntCellRenderer(NumberFormat numberFormat) {    super();    setHorizontalAlignment(JLabel.RIGHT);    this.setVerticalAlignment(JLabel.TOP);    this.numberFormat = numberFormat;  }  /**   * 整数靠右显示   */  public IntCellRenderer() {    super();    setHorizontalAlignment(JLabel.RIGHT);    this.setVerticalAlignment(JLabel.TOP);    this.numberFormat = NumberFormat.getInstance();    numberFormat.setMinimumFractionDigits(0);    numberFormat.setMaximumFractionDigits(0);  }  public Component getTableCellRendererComponent(JTable table, Object value,  boolean isSelected, boolean hasFocus, int row, int column) {    try {      value = numberFormat.format(value);    } catch (Exception e) {    }    return super.getTableCellRendererComponent(table, value, isSelected, hasFocus,    row, column);  }}