package com.ufgov.zc.client.component.table.cellrenderer;import java.awt.Component;import javax.swing.JTable;import javax.swing.JTextArea;import javax.swing.table.DefaultTableCellRenderer;import javax.swing.table.TableCellRenderer;public class LineWrapCellRenderer extends JTextArea implements TableCellRenderer {  private static final long serialVersionUID = 6661718059329644534L;  private final DefaultTableCellRenderer defaultCellRenderer = new DefaultTableCellRenderer();  public LineWrapCellRenderer() {    setLineWrap(true);    setWrapStyleWord(false);    setEditable(false);  }  public Component getTableCellRendererComponent(final JTable table, Object value,  boolean isSelected, boolean hasFocus, final int row, int column) {    defaultCellRenderer.getTableCellRendererComponent(table, value, isSelected,    hasFocus, row, column);    setForeground(defaultCellRenderer.getForeground());    setBackground(defaultCellRenderer.getBackground());    setBorder(defaultCellRenderer.getBorder());    setFont(defaultCellRenderer.getFont());    setText(defaultCellRenderer.getText());    return this;  }}