package com.ufgov.zc.client.component.table.codecelleditor;import java.awt.Component;import javax.swing.JTable;import javax.swing.table.TableCellEditor;import com.ufgov.zc.client.component.element.FundComboBox;import com.ufgov.zc.client.component.table.GkAbstractCellEditor;import com.ufgov.zc.common.commonbiz.model.Fund;public class FundComboBoxCellEditor extends GkAbstractCellEditor implements TableCellEditor {  private static final long serialVersionUID = -1014964575090820890L;  private FundComboBox editorComponent;  {    editorComponent = new FundComboBox();    editorComponent.initComboBox();  }  public FundComboBoxCellEditor() {  }  public Component getTableCellEditorComponent(JTable table, Object value, boolean isSelected, int row,  int column) {    editorComponent.setSelectedFundByCode((String) value);    return editorComponent;  }  public Object getCellEditorValue() {    String code = null;    Fund value = editorComponent.getSelectedFund();    if (value != null) {      code = value.getCode();    }    return code;  }}