package com.ufgov.zc.client.component.table.codecelleditor;import java.awt.Component;import javax.swing.JTable;import javax.swing.table.TableCellEditor;import com.ufgov.zc.client.common.BillElementMeta;import com.ufgov.zc.client.component.element.InceptDocTableSelectField;import com.ufgov.zc.client.component.table.GkAbstractCellEditor;import com.ufgov.zc.common.commonbiz.model.InceptDoc;public class InceptDocCellEditor extends GkAbstractCellEditor implements TableCellEditor {  private static final long serialVersionUID = 399417943037425222L;  private InceptDocTableSelectField editorComponent = new InceptDocTableSelectField(20);  public InceptDocCellEditor() {  }  public InceptDocCellEditor(BillElementMeta bem) {    editorComponent.setBillElementMeta(bem);  }  public Component getTableCellEditorComponent(JTable table, Object value, boolean isSelected, int row,  int column) {    editorComponent.setValueByCode((String) value);    return editorComponent;  }  public Object getCellEditorValue() {    String code = null;    InceptDoc value = editorComponent.getInceptDoc();    if (value != null) {      code = value.getCode();    }    return code;  }}