package com.ufgov.zc.client.component.table.codecelleditor;import java.awt.Component;import javax.swing.AbstractCellEditor;import javax.swing.JTable;import javax.swing.table.TableCellEditor;import com.ufgov.zc.client.component.MonthComboBox;import com.ufgov.zc.common.system.model.AsVal;public class MonthComboCellEditor extends AbstractCellEditor implements TableCellEditor {  private MonthComboBox planMothCombox = new MonthComboBox(false);  public MonthComboCellEditor() {  }  public MonthComboCellEditor(int curMonth) {    planMothCombox.setSelectedAsValByCode(Integer.toString(curMonth));  }  public Component getTableCellEditorComponent(JTable table, Object value, boolean isSelected, int row,  int column) {    planMothCombox.setSelectedAsValByCode(value + "");    return planMothCombox;  }  public Object getCellEditorValue() {    Object obj = planMothCombox.getSelectedAsVal();    if (obj != null) {      AsVal asVal = (AsVal) obj;      return new Integer(asVal.getValId().trim());    } else {      return 0;    }  }}