package com.ufgov.zc.client.component.table.codecelleditor;import java.awt.Component;import javax.swing.JTable;import javax.swing.table.TableCellEditor;import com.ufgov.zc.client.common.BillElementMeta;import com.ufgov.zc.client.component.element.SendDocTreeSelectField;import com.ufgov.zc.client.component.table.GkAbstractCellEditor;import com.ufgov.zc.common.commonbiz.model.SendDoc;public class SendDocTreeCellEditor extends GkAbstractCellEditor implements TableCellEditor {  private static final long serialVersionUID = -393237123566353415L;  private SendDocTreeSelectField editorComponent = new SendDocTreeSelectField(20);  public SendDocTreeCellEditor() {  }  public SendDocTreeCellEditor(BillElementMeta bem) {    editorComponent.setBillElementMeta(bem);  }  public Component getTableCellEditorComponent(JTable table, Object value, boolean isSelected, int row,  int column) {    editorComponent.setValueByCode((String) value);    return editorComponent;  }  public Object getCellEditorValue() {    String code = null;    SendDoc value = editorComponent.getSendDoc();    if (value != null) {      code = value.getCode();    }    return code;  }}