package com.ufgov.zc.client.component.ui.conditionitem;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.JComponent;import com.ufgov.zc.client.component.AgentBankComboBox;public class BbAgentBankConditionItem extends AbstractSearchConditionItem {  private AgentBankComboBox comboBox;  private String bankType = AgentBankComboBox.BANK_TYPE_ALL;  public BbAgentBankConditionItem(String displayName, Object defaultValue) {    init(displayName);  }  public BbAgentBankConditionItem(String displayName, Object defaultValue, String bankType) {    this.bankType = bankType;    init(displayName);  }  @Override  protected JComponent createEditorComponent() {    comboBox = new AgentBankComboBox(bankType);    comboBox.addActionListener(new ActionListener() {      public void actionPerformed(ActionEvent event) {        fireSearch();        fireValueChanged();      }    });    return comboBox;  }  @Override  public Object getValue() {    if (comboBox.getProxyBank() != null) {      return comboBox.getProxyBank().getCode();    } else {      return null;    }  }  @Override  public void setValue(Object value) {  }}