package com.ufgov.zc.client.component.ui.conditionitem;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.JComponent;import com.ufgov.zc.client.component.CompanyComboBox;public class CompanyComboboxConditionItem extends AbstractSearchConditionItem {  private String dataRuleId;  private Object defaultValue;  public CompanyComboboxConditionItem(String displayName) {    init(displayName);  }  public CompanyComboboxConditionItem(String displayName, Object defaultValue) {    this.defaultValue = defaultValue;    init(displayName);  }  public CompanyComboboxConditionItem(String displayName, String dataRuleId) {    this.dataRuleId = dataRuleId;    init(displayName);  }  private CompanyComboBox companyComboBox;  @Override  protected JComponent createEditorComponent() {    if (companyComboBox == null) {      companyComboBox = new CompanyComboBox(dataRuleId);      companyComboBox.addActionListener(new ActionListener() {        public void actionPerformed(ActionEvent e) {          fireSearch();          fireValueChanged();        }      });    }    return companyComboBox;  }  @Override  public void setValue(Object value) {    companyComboBox.setSelectedItem(value);  }  @Override  public Object getValue() {    return companyComboBox.getSelectedItem();  }}