package com.ufgov.zc.client.component.ui.conditionitem;import java.awt.BorderLayout;import java.awt.Component;import java.awt.Dimension;import java.util.Date;import javax.swing.JComponent;import javax.swing.JDialog;import javax.swing.SwingUtilities;import javax.swing.UIManager;import javax.swing.UnsupportedLookAndFeelException;import com.ufgov.zc.client.component.DateField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;/** * <p>Title: GK</p> * <p>Description:日期型式的搜索条件项</p> * <p>Copyright: Copyright 2009 ufgov, Inc.</p> * <p>Company: ufgov</p> * <p>创建时间: 2009-4-21</p> * @author 刘永伟(manlge) * @version 1.0 */public class DateSearchConditionItem extends AbstractSearchConditionItem {  private DateField dateTextField;  public DateSearchConditionItem(String displayName) {    init(displayName);  }  public DateSearchConditionItem(String displayName, String dateString) {    init(displayName);    getDateTextField().setText(dateString);  }  @Override  protected JComponent createEditorComponent() {    return getDateTextField();  }  private DateField getDateTextField() {    if (dateTextField == null) {      dateTextField = new DateField();      dateTextField.addValueChangeListener(new ValueChangeListener() {        public void valueChanged(ValueChangeEvent e) {          fireSearch();          fireValueChanged();        }      });      dateTextField.setPreferredSize(new Dimension(120, 20));    }    return dateTextField;  }  @Override  public void setValue(Object value) {    if (value == null || value instanceof String) {      dateTextField.setText((String) value);    }  }  public void setDate(Date date) {    dateTextField.setDate(date);  }  @Override  public Object getValue() {    return dateTextField.getText();  }  public Date getDate() {    return dateTextField.getDate();  }    public static void main(String[] args) throws ClassNotFoundException, InstantiationException, IllegalAccessException,  UnsupportedLookAndFeelException {    UIManager.setLookAndFeel(UIManager.getSystemLookAndFeelClassName());    JDialog f = new JDialog();    f.setBounds(0, 0, 320, 240);    DateSearchConditionItem dateTextField = new DateSearchConditionItem("date", "2012-11-21");        dateTextField.setEnabled(true);    f.add(dateTextField, BorderLayout.NORTH);    SwingUtilities.updateComponentTreeUI(f);    f.setVisible(true);  }}