package com.ufgov.zc.client.component.ui.conditionitem;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.JComponent;import com.ufgov.zc.client.component.ExportStatusComboBox;import com.ufgov.zc.common.system.dto.ElementConditionDto;public class ExportStatusConditionItem extends AbstractSearchConditionItem {  public ExportStatusConditionItem(String displayName) {    init(displayName);  }  private ExportStatusComboBox exportStatusComboBox;  @Override  protected JComponent createEditorComponent() {    if (exportStatusComboBox == null) {      exportStatusComboBox = new ExportStatusComboBox();      exportStatusComboBox.addActionListener(new ActionListener() {        public void actionPerformed(ActionEvent e) {          fireSearch();          fireValueChanged();        }      });    }    return exportStatusComboBox;  }  @Override  public void setValue(Object value) {    exportStatusComboBox.setSelectedItem(value);  }  @Override  public Object getValue() {    return exportStatusComboBox.getSelectedItem();  }  public void putToElementConditionDto(ElementConditionDto element) {    String value = (String) exportStatusComboBox.getSelectedItem();    if (value.equals("Y")) {      element.setExportTimes(1);    } else if (value.equals("N")) {      element.setExportTimes(0);    } else {      element.setExportTimes(null);    }  }}