package com.ufgov.zc.client.component.ui.conditionitem;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.FundForBiXJComboBox;public class FundForBiXJCboxConditionItem extends AbstractSearchConditionItem {  public FundForBiXJCboxConditionItem(String displayName) {    init(displayName);  }  public FundForBiXJComboBox fundComboBox;  protected JComponent createEditorComponent() {    if (fundComboBox == null) {      fundComboBox = new FundForBiXJComboBox();      fundComboBox.initComboBox();      fundComboBox.addActionListener(new ActionListener() {        public void actionPerformed(ActionEvent e) {          fireSearch();          fireValueChanged();        }      });    }    return fundComboBox;  }  public void setValue(Object value) {    fundComboBox.setSelectedItem(value);  }  @Override  public Object getValue() {    return fundComboBox.getSelectedItem();  }  public void setControlLevel(int level) {    fundComboBox.setCtrlLevelNum(level);    fundComboBox.initComboBox();  }}