package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.FundTreeSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.Fund;public class FundTreeSelectFieldConditionItem extends AbstractSearchConditionItem {  private String compoId;  public FundTreeSelectFieldConditionItem(String displayName) {    init(displayName);  }  public FundTreeSelectFieldConditionItem(String displayName, String compoId) {    this.compoId = compoId;    init(displayName);  }  private FundTreeSelectField selectField;  @Override  protected JComponent createEditorComponent() {    if (selectField == null) {      selectField = new FundTreeSelectField(18, compoId);      selectField.addValueChangeListener(new ValueChangeListener() {        public void valueChanged(ValueChangeEvent e) {          Fund value = selectField.getFund();          String code = null;          if (value != null) {            code = value.getCode();          }          fireSearch();          fireValueChanged();        }      });    }    return selectField;  }  @Override  public void setValue(Object value) {    selectField.setFund((Fund) value);  }  @Override  public Object getValue() {    return selectField.getFund();  }}