package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.InceptDocTableSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.InceptDoc;public class InceptDocTableSelectFieldConditionItem extends AbstractSearchConditionItem {  public InceptDocTableSelectFieldConditionItem(String displayName) {    init(displayName);  }  private InceptDocTableSelectField inceptDocTableSelectField;  @Override  protected JComponent createEditorComponent() {    if (inceptDocTableSelectField == null) {      inceptDocTableSelectField = new InceptDocTableSelectField(20);      inceptDocTableSelectField.addValueChangeListener(new ValueChangeListener() {        public void valueChanged(ValueChangeEvent e) {          InceptDoc value = inceptDocTableSelectField.getInceptDoc();          String code = null;          if (value != null) {            code = value.getCode();          }          fireSearch();          fireValueChanged();        }      });    }    return inceptDocTableSelectField;  }  @Override  public void setValue(Object value) {    inceptDocTableSelectField.setInceptDoc((InceptDoc) value);  }  @Override  public Object getValue() {    return inceptDocTableSelectField.getInceptDoc();  }}