package com.ufgov.zc.client.component.ui.conditionitem;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.ManageForBiXJComboBox;public class ManageForBiXJCboxConditionItem extends AbstractSearchConditionItem {  public ManageForBiXJCboxConditionItem(String displayName) {    init(displayName);  }  private ManageForBiXJComboBox manageComboBox;  @Override  protected JComponent createEditorComponent() {    if (manageComboBox == null) {      manageComboBox = new ManageForBiXJComboBox();      manageComboBox.initComboBox();      manageComboBox.addActionListener(new ActionListener() {        public void actionPerformed(ActionEvent e) {          fireSearch();          fireValueChanged();        }      });    }    return manageComboBox;  }  @Override  public void setValue(Object value) {    manageComboBox.setSelectedItem(value);  }  @Override  public Object getValue() {    return manageComboBox.getSelectedItem();  }}