package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.ManageTableSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.Manage;public class ManageTableSelectFieldConditionItem extends AbstractSearchConditionItem {  private String dataRuleId;  public ManageTableSelectFieldConditionItem(String displayName) {    init(displayName);  }  public ManageTableSelectFieldConditionItem(String displayName, String dataRuleId) {    this.dataRuleId = dataRuleId;    init(displayName);  }  private ManageTableSelectField manageTableSelectField;  @Override  protected JComponent createEditorComponent() {    if (manageTableSelectField == null) {      manageTableSelectField = new ManageTableSelectField(dataRuleId, 20);      manageTableSelectField.addValueChangeListener(new ValueChangeListener() {        public void valueChanged(ValueChangeEvent e) {          fireSearch();          fireValueChanged();        }      });    }    return manageTableSelectField;  }  @Override  public void setValue(Object value) {    manageTableSelectField.setManage((Manage) value);  }  @Override  public Object getValue() {    return manageTableSelectField.getManage();  }}