package com.ufgov.zc.client.component.ui.conditionitem;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.util.ArrayList;import java.util.List;import javax.swing.JComponent;import com.ufgov.zc.client.component.OperationTypeComboBox;import com.ufgov.zc.common.commonbiz.model.OperationType;public class OperationTypeConditionItem extends AbstractSearchConditionItem {  private static final long serialVersionUID = -4168197873464735497L;  private OperationTypeComboBox checkBox;  private List flowCodeList = new ArrayList();  public OperationTypeConditionItem(String displayName, Object module) {    if ("AM".equals(module)) {      flowCodeList.add("02");      flowCodeList.add("03");    } else if ("CP".equals(module)) {      flowCodeList.add("01");      flowCodeList.add("03");    }    init(displayName);  }  @Override  protected JComponent createEditorComponent() {    checkBox = new OperationTypeComboBox(flowCodeList);    checkBox.initComboBox();    checkBox.addActionListener(new ActionListener() {      public void actionPerformed(ActionEvent event) {        fireSearch();        fireValueChanged();      }    });    return checkBox;  }  @Override  public Object getValue() {    OperationType type = checkBox.getSelectedOperationType();    if (type != null) {      return type.getCode();    } else {      return null;    }  }  @Override  public void setValue(Object value) {    checkBox.setSelectedItem(null);  }  public void setControlLevel(int level) {    checkBox.setCtrlLevelNum(level);    checkBox.initComboBox();  }}