package com.ufgov.zc.client.component.ui.conditionitem;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.OrgComboBox;public class OrgComboBoxConditionItem extends AbstractSearchConditionItem {  private String compoId;  public OrgComboBoxConditionItem(String displayName) {    init(displayName);  }  public OrgComboBoxConditionItem(String displayName, String compoId) {    this.compoId = compoId;    init(displayName);  }  private OrgComboBox orgComboBox;  @Override  protected JComponent createEditorComponent() {    if (orgComboBox == null) {      orgComboBox = new OrgComboBox(compoId);      orgComboBox.initComboBox();      orgComboBox.addActionListener(new ActionListener() {        public void actionPerformed(ActionEvent e) {          fireSearch();          fireValueChanged();        }      });    }    return orgComboBox;  }  @Override  public void setValue(Object value) {    orgComboBox.setSelectedItem(value);  }  @Override  public Object getValue() {    return orgComboBox.getSelectedItem();  }}