package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.OriginTableSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.Origin;public class OriginTableSelectFieldConditionItem extends AbstractSearchConditionItem {  /**   *    */  private static final long serialVersionUID = -6715296965066934093L;  private String dataRuleId;  public OriginTableSelectFieldConditionItem(String displayName) {    init(displayName);  }  public OriginTableSelectFieldConditionItem(String displayName, String dataRuleId) {    this.dataRuleId = dataRuleId;    init(displayName);  }  private OriginTableSelectField originTableSelectField;  @Override  protected JComponent createEditorComponent() {    if (originTableSelectField == null) {      originTableSelectField = new OriginTableSelectField(dataRuleId, 20);      originTableSelectField.addValueChangeListener(new ValueChangeListener() {        public void valueChanged(ValueChangeEvent e) {          fireSearch();          fireValueChanged();        }      });    }    return originTableSelectField;  }  @Override  public void setValue(Object value) {    originTableSelectField.setOrigin((Origin) value);  }  @Override  public Object getValue() {    return originTableSelectField.getOrigin();  }}