package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.OriginTreeSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.Origin;public class OriginTreeSelectFieldConditionItem extends AbstractSearchConditionItem {  /**   *    */  private static final long serialVersionUID = -6715296965066934093L;  private String dataRuleId;  public OriginTreeSelectFieldConditionItem(String displayName) {    init(displayName);  }  public OriginTreeSelectFieldConditionItem(String displayName, String dataRuleId) {    this.dataRuleId = dataRuleId;    init(displayName);  }  private OriginTreeSelectField selectField;  @Override  protected JComponent createEditorComponent() {    if (selectField == null) {      selectField = new OriginTreeSelectField(dataRuleId, 20);      selectField.addValueChangeListener(new ValueChangeListener() {        public void valueChanged(ValueChangeEvent e) {          fireSearch();          fireValueChanged();        }      });    }    return selectField;  }  @Override  public void setValue(Object value) {    selectField.setOrigin((Origin) value);  }  @Override  public Object getValue() {    return selectField.getOrigin();  }}