package com.ufgov.zc.client.component.ui.conditionitem;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.util.ArrayList;import java.util.List;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.PaytypeComboBox;public class PayTypeComboboxConditionItem extends AbstractSearchConditionItem {  /**   *    */  private static final long serialVersionUID = -5262048321256204798L;  private String dataRuleId = null;  private List filterList = null;  public PayTypeComboboxConditionItem(String displayName) {    init(displayName);  }  public PayTypeComboboxConditionItem(String displayName, Object defaultValue) {    if (defaultValue != null) {      if (defaultValue instanceof ArrayList) {        ArrayList filterList = (ArrayList) defaultValue;        this.filterList = filterList;      }    }    init(displayName);  }  public PayTypeComboboxConditionItem(String displayName, String dataRuleId) {    this.dataRuleId = dataRuleId;    init(displayName);  }  private PaytypeComboBox payTypeComboBox;  protected JComponent createEditorComponent() {    if (payTypeComboBox == null) {      if (filterList != null) {        payTypeComboBox = new PaytypeComboBox(filterList, false);      } else {        payTypeComboBox = new PaytypeComboBox(dataRuleId, false);      }      payTypeComboBox.initComboBox();      payTypeComboBox.addActionListener(new ActionListener() {        public void actionPerformed(ActionEvent e) {          fireSearch();          fireValueChanged();        }      });    }    return payTypeComboBox;  }  @Override  public void setValue(Object value) {    payTypeComboBox.setSelectedItem(value);  }  @Override  public Object getValue() {    return payTypeComboBox.getSelectedItem();  }}