package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.PayoutTableSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.Payout;public class PayoutTableSelectFieldConditionItem extends AbstractSearchConditionItem {  public PayoutTableSelectFieldConditionItem(String displayName) {    init(displayName);  }  private PayoutTableSelectField payoutTableSelectField;  @Override  protected JComponent createEditorComponent() {    if (payoutTableSelectField == null) {      payoutTableSelectField = new PayoutTableSelectField(20);      payoutTableSelectField.addValueChangeListener(new ValueChangeListener() {        public void valueChanged(ValueChangeEvent e) {          fireSearch();          fireValueChanged();        }      });    }    return payoutTableSelectField;  }  @Override  public void setValue(Object value) {    payoutTableSelectField.setPayout((Payout) value);  }  @Override  public Object getValue() {    return payoutTableSelectField.getPayout();  }}