package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.SendDocTableSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.SendDoc;public class SendDocTableSelectFieldConditionItem extends AbstractSearchConditionItem {  public SendDocTableSelectFieldConditionItem(String displayName) {    init(displayName);  }  private SendDocTableSelectField sendDocTableSelectField;  @Override  protected JComponent createEditorComponent() {    if (sendDocTableSelectField == null) {      sendDocTableSelectField = new SendDocTableSelectField(20);      sendDocTableSelectField.addValueChangeListener(new ValueChangeListener() {        public void valueChanged(ValueChangeEvent e) {          SendDoc value = sendDocTableSelectField.getSendDoc();          String code = null;          if (value != null) {            code = value.getCode();          }          fireSearch();          fireValueChanged();        }      });    }    return sendDocTableSelectField;  }  @Override  public void setValue(Object value) {    sendDocTableSelectField.setSendDoc((SendDoc) value);  }  @Override  public Object getValue() {    return sendDocTableSelectField.getSendDoc();  }}