package com.ufgov.zc.client.component.ui.fieldeditor;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.FundComboBox;import com.ufgov.zc.common.commonbiz.model.BaseBill;import com.ufgov.zc.common.commonbiz.model.Fund;public class FundComboBoxFieldEditor extends AbstractFieldEditor {  /**   *    */  private static final long serialVersionUID = -4691003073875144404L;  private FundComboBox editField;  public FundComboBoxFieldEditor(String name) {    super(name);  }  public FundComboBoxFieldEditor(String name, boolean isEnabled) {    super(name);    editField.setEnabled(isEnabled);    editField.setEditable(isEnabled);  }  protected JComponent createEditorComponent() {    editField = new FundComboBox();    editField.initComboBox();    editField.addActionListener(new ActionListener() {      public void actionPerformed(ActionEvent e) {        syncObject();      }    });    return editField;  }  /**   * 同步到对象   */  private void syncObject() {    if (getEditObject() instanceof BaseBill) {      BaseBill bill = (BaseBill) getEditObject();      if (bill != null) {        Fund value = editField.getSelectedFund();        bill.setFundCode(value == null ? null : value.getCode());      }    }    this.fireEditSynced();  }  public Object getValue() {    return editField.getSelectedFund();  }  public void setValue(Object value) {    if (value == null) {      editField.setSelectedFund(null);    } else if (value instanceof BaseBill) {      BaseBill bill = (BaseBill) value;      editField.setSelectedFundByCode(bill.getFundCode());    }  }  public void setFieldEditorCode() {    this.FieldEditorCode = FieldEditorCodeConstants.FUND_CODE;  }}