package com.ufgov.zc.client.component.ui.fieldeditor;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.util.ArrayList;import java.util.List;import javax.swing.JComboBox;import javax.swing.JComponent;import com.ufgov.zc.client.component.OperationTypeComboBox;import com.ufgov.zc.common.commonbiz.aware.OperationTypeAware;import com.ufgov.zc.common.commonbiz.model.OperationType;public class OperationTypeComboBoxFieldEditor extends AbstractFieldEditor {  private OperationTypeComboBox box;  private List flowCodeList = new ArrayList();  public OperationTypeComboBoxFieldEditor(String name) {    super(name);  }  public OperationTypeComboBoxFieldEditor(String name, boolean isEnabled) {    super(name);    box.setEnabled(isEnabled);  }  public OperationTypeComboBoxFieldEditor(String name, boolean isEnabled, List flowCodeList) {    this.flowCodeList = flowCodeList;    this.init(name);    box.setEnabled(isEnabled);  }  @Override  public Object getValue() {    return box.getSelectedItem();  }  @Override  public void setValue(Object value) {    if (value instanceof OperationTypeAware) {      OperationTypeAware operationTypeAware = (OperationTypeAware) value;      box.setSelectedOperationTypeByCode(operationTypeAware.getOperationTypeCode());    }  }  public JComboBox getComboBox() {    return this.box;  }  @Override  protected JComponent createEditorComponent() {    box = new OperationTypeComboBox(flowCodeList);    box.initComboBox();    box.addActionListener(new ActionListener() {      public void actionPerformed(ActionEvent e) {        if (getEditObject() instanceof OperationTypeAware) {          OperationTypeAware operationTypeAware = (OperationTypeAware) getEditObject();          if (operationTypeAware != null) {            OperationType operationType = box.getSelectedOperationType();            operationTypeAware.setOperationTypeCode(operationType == null ? null : operationType.getCode());          }          fireEditSynced();        }      }    });    return box;  }  public void setFieldEditorCode() {    this.FieldEditorCode = FieldEditorCodeConstants.OPERATION_TYPE_CODE;  }}