package com.ufgov.zc.client.component.ui.fieldeditor;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.PayoutTableSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.BaseBill;import com.ufgov.zc.common.commonbiz.model.Payout;public class PayoutTableSelectFieldEditor extends AbstractFieldEditor {  /**   *    */  private static final long serialVersionUID = -7677429808342983951L;  public PayoutTableSelectField editField;  public PayoutTableSelectFieldEditor(String name) {    super(name);  }  public PayoutTableSelectFieldEditor(String name, boolean isEnabled) {    super(name);    editField.setEnabled(isEnabled);    editField.setEditable(isEnabled);  }  protected JComponent createEditorComponent() {    editField = new PayoutTableSelectField();    editField.addValueChangeListener(new ValueChangeListener() {      public void valueChanged(ValueChangeEvent e) {        syncEditObject();      }    });    return editField;  }  /**   * 同步到对象   */  protected void syncEditObject() {    if (getEditObject() instanceof BaseBill) {      BaseBill bill = (BaseBill) getEditObject();      if (bill != null) {        Payout Payout = editField.getPayout();        bill.setPayoutCode(Payout == null ? null : Payout.getCode());      }    }    this.fireEditSynced();  }  public Object getValue() {    return editField.getPayout();  }  public void setValue(Object value) {    if (value == null) {      editField.setValue(null);    } else if (value instanceof BaseBill) {      BaseBill bill = (BaseBill) value;      editField.setValueByCode(bill.getPayoutCode());    }  }  public void setFieldEditorCode() {    this.FieldEditorCode = FieldEditorCodeConstants.PAYOUT_CODE;  }}