package com.ufgov.zc.client.component.ui.fieldeditor;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.ProjectTreeSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.BaseBill;import com.ufgov.zc.common.commonbiz.model.Project;public class ProjectTreeSelectFieldEditor extends AbstractFieldEditor {  /**   *    */  private static final long serialVersionUID = -7677429808342983951L;  private ProjectTreeSelectField editField;  public ProjectTreeSelectFieldEditor(String name) {    super(name);  }  public ProjectTreeSelectFieldEditor(String name, boolean isEnabled) {    super(name);    editField.setEnabled(isEnabled);    editField.setEditable(isEnabled);  }  protected JComponent createEditorComponent() {    editField = new ProjectTreeSelectField();    editField.addValueChangeListener(new ValueChangeListener() {      public void valueChanged(ValueChangeEvent e) {        syncEditObject();      }    });    return editField;  }  /**   * 同步到对象   */  protected void syncEditObject() {    if (getEditObject() instanceof BaseBill) {      BaseBill bill = (BaseBill) getEditObject();      if (bill != null) {        Project Project = editField.getProject();        bill.setProjectCode(Project == null ? null : Project.getCode());      }    }    this.fireEditSynced();  }  public Object getValue() {    return editField.getProject();  }  public void setValue(Object value) {    if (value == null) {      editField.setValue(null);    } else if (value instanceof BaseBill) {      BaseBill bill = (BaseBill) value;      editField.setValueByCode(bill.getProjectCode());    }  }  public void setFieldEditorCode() {    this.FieldEditorCode = FieldEditorCodeConstants.PROJECT_CODE;  }}