package com.ufgov.zc.client.component.zc;import java.util.List;import javax.swing.table.TableModel;import com.ufgov.zc.client.common.MyTableModel;import com.ufgov.zc.common.zc.foreignentity.IForeignEntityHandler;import com.ufgov.zc.common.zc.model.ZcPProMake;public abstract class ZcEbProjectMakeHandler implements IForeignEntityHandler {  private boolean isMultipleSelect;  private String sqlId = "ZC_P_PRO_MAKE.foreignSelection";  private String columNames[] = { "项目编号", "项目名称", "采购单位", "联系人" };;  public ZcEbProjectMakeHandler() {    this(false);  }  public ZcEbProjectMakeHandler(boolean isMultipleSelect) {    this.isMultipleSelect = isMultipleSelect;  }  public abstract void excute(List selectedDatas);  public TableModel createTableModel(List showDatas) {    Object data[][] = new Object[showDatas.size()][columNames.length];    for (int i = 0; i < showDatas.size(); i++) {      ZcPProMake rowData = (ZcPProMake) showDatas.get(i);      int col = 0;      data[i][col++] = rowData.getZcMakeCode();      data[i][col++] = rowData.getZcMakeName();    }    MyTableModel model = new MyTableModel(data, columNames) {      public boolean isCellEditable(int row, int colum) {        return false;      }    };    return model;  }  public boolean isMultipleSelect() {    return this.isMultipleSelect;  }  public String[] getColumNames() {    return columNames;  }  public void setColumNames(String[] columNames) {    this.columNames = columNames;  }  public String getSqlId() {    return sqlId;  }  public void setSqlId(String sqlId) {    this.sqlId = sqlId;  }}