package com.ufgov.zc.client.component.zc;import java.util.List;import javax.swing.table.TableModel;import org.apache.commons.beanutils.MethodUtils;import com.ufgov.zc.client.common.MyTableModel;import com.ufgov.zc.common.zc.foreignentity.IForeignEntityHandler;import com.ufgov.zc.common.zc.model.ZcEbSupplier;public class ZcEbProviderHandler implements IForeignEntityHandler {  private String columNames[] = {"供应商名称", "供应商联系人"};  private AbstractMainSubEditPanel editPanel;  public ZcEbProviderHandler(String columNames[], AbstractMainSubEditPanel editPanel) {    this.columNames = columNames;    this.editPanel = editPanel;  }  public ZcEbProviderHandler(AbstractMainSubEditPanel editPanel) {    this.editPanel = editPanel;  }  public void excute(List selectedDatas) {    if(selectedDatas == null || selectedDatas.size() == 0){      return;    }    ZcEbSupplier supplier = (ZcEbSupplier) selectedDatas.get(0);    try {      MethodUtils.invokeExactMethod(editPanel, "onClickProvider", supplier);    } catch (Exception e) {    }  }  @Override  public TableModel createTableModel(List showDatas) {    Object data[][] = new Object[showDatas.size()][columNames.length];    for (int i = 0; i < showDatas.size(); i++) {      ZcEbSupplier rowData = (ZcEbSupplier) showDatas.get(i);      int col = 0;      data[i][col++] = rowData.getName();      data[i][col++] = rowData.getLinkMan();    }    MyTableModel model = new MyTableModel(data, columNames) {      public boolean isCellEditable(int row, int colum) {        return false;      }    };    return model;  }  @Override  public boolean isMultipleSelect() {    return false;  }  public void afterClear(){    try {      MethodUtils.invokeExactMethod(editPanel, "onClearProvider", null);    } catch (Exception e) {    }  }  public static String[] getColumNames() {    return new String[] {"供应商名称", "供应商联系人"};  }}