package com.ufgov.zc.client.component.zc;import java.util.List;import javax.swing.table.TableModel;import com.ufgov.zc.client.common.MyTableModel;import com.ufgov.zc.common.zc.foreignentity.IForeignEntityHandler;import com.ufgov.zc.common.zc.model.EmExpertType;public abstract class ZcEmExpertTypeHandler implements IForeignEntityHandler {  private boolean isMultipleSelect;  private String sqlId = "EmExpertType.list";  private String columNames[] = { "类别编号", "类别名称" };  public ZcEmExpertTypeHandler() {    this(false);  }  public ZcEmExpertTypeHandler(boolean isMultipleSelect) {    this.isMultipleSelect = isMultipleSelect;  }  public abstract void excute(List selectedDatas);  @SuppressWarnings("unchecked")  @Override  public TableModel createTableModel(List showDatas) {    Object data[][] = new Object[showDatas.size()][columNames.length];    for (int i = 0; i < showDatas.size(); i++) {      EmExpertType rowData = (EmExpertType) showDatas.get(i);      int col = 0;      data[i][col++] = rowData.getEmTypeCode();      data[i][col++] = rowData.getEmTypeName();    }    MyTableModel model = new MyTableModel(data, columNames) {      private static final long serialVersionUID = -1045880580648402603L;      public boolean isCellEditable(int row, int colum) {        return false;      }    };    return model;  }  public boolean isMultipleSelect() {    return this.isMultipleSelect;  }  public String[] getColumNames() {    return columNames;  }  public void setColumNames(String[] columNames) {    this.columNames = columNames;  }  public String getSqlId() {    return sqlId;  }  public void setSqlId(String sqlId) {    this.sqlId = sqlId;  }}