package com.ufgov.zc.client.component.zc.fieldeditor.emexperttype;import java.awt.BorderLayout;import java.awt.GridLayout;import java.awt.event.WindowAdapter;import java.awt.event.WindowEvent;import javax.swing.JFrame;import javax.swing.JPanel;import com.ufgov.zc.client.component.JButtonTextField;import com.ufgov.zc.client.component.zc.fieldeditor.TextFieldEditor;import com.ufgov.zc.common.zc.model.EmExpertTypeSelection;public class EmExpertTypeSelectionTreeSelectField extends JButtonTextField {  private static final long serialVersionUID = 2297412181958977216L;  public EmExpertTypeSelectionTreeSelectField() {    super(20);    selectDialog = new EmExpertTypeSelectionTreeSelectDialog(owner, true, this);  }  public void setValue(Object value) {    this.value = value;    if (value != null) {      EmExpertTypeSelection data = (EmExpertTypeSelection) value;      this.setText(data.getEmExpertType().getEmTypeCode());      this.setToolTipText("[" + data.getEmExpertType().getEmTypeCode() + "]"      + data.getEmExpertType().getEmTypeName());    } else {      this.setText("");      this.setToolTipText(null);    }    this.fireValueChanged();  }  public void setEmExpertTypeSelection(EmExpertTypeSelection value) {    this.setValue(value);  }  public EmExpertTypeSelection getEmExpertTypeSelection() {    return (EmExpertTypeSelection) this.getValue();  }  public void handleClick(JButtonTextField jButtonTextField) {    await();    selectDialog.setVisible(true);  }  public static void main(String[] args) {    JFrame f = new JFrame();    TextFieldEditor name = new TextFieldEditor("名称", "zcCatalogueName");    EmExpertTypeSelectionTreeSelectField code = new EmExpertTypeSelectionTreeSelectField();    code.setValueByCode("000101");    code.setEditable(false);    code.setEnabled(false);    code.setEnabled(true);    name.setEnabled(true);    JPanel panel = new JPanel(new GridLayout(1, 2));    panel.add(code);    panel.add(name);    f.getContentPane().add(panel, BorderLayout.NORTH);    f.setSize(400, 300);    f.setLocationRelativeTo(null);    f.setVisible(true);    f.addWindowListener(new WindowAdapter() {      public void windowClosing(WindowEvent e) {        System.exit(0);      }    });  }}