package com.ufgov.zc.client.component.zc.fieldeditor.zcbagency;import java.io.Serializable;import javax.swing.JComponent;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.client.component.ui.fieldeditor.AbstractFieldEditor;import com.ufgov.zc.client.component.zc.fieldeditor.TextFieldEditor;import com.ufgov.zc.common.commonbiz.model.Company;import com.ufgov.zc.common.system.util.BeanUtil;import com.ufgov.zc.common.zc.model.ZcBAgency;import com.ufgov.zc.common.zc.model.ZcBaseBill;public class ZcBAgencySelectEditor extends AbstractFieldEditor {  private static final long serialVersionUID = -7677429808342983951L;  private ZcBAgencySelectField coField;  private TextFieldEditor zcAgeyName;  public ZcBAgencySelectEditor(String name, String fieldName, TextFieldEditor zcAgeyName) {    this.fieldName = fieldName;    this.zcAgeyName = zcAgeyName;    init(name);  }  public ZcBAgencySelectEditor(String name, boolean isEnabled, String fieldName, TextFieldEditor zcAgeyName) {    this.fieldName = fieldName;    this.zcAgeyName = zcAgeyName;    init(name);    coField.setEnabled(isEnabled);    coField.setEditable(isEnabled);  }  protected JComponent createEditorComponent() {    coField = new ZcBAgencySelectField(zcAgeyName);    coField.addValueChangeListener(new ValueChangeListener() {      public void valueChanged(ValueChangeEvent e) {        syncEditObject();      }    });    return coField;  }  protected void syncEditObject() {    if (getEditObject() instanceof ZcBaseBill) {      ZcBaseBill bill = (ZcBaseBill) getEditObject();      if (bill != null) {        ZcBAgency zcBAgency = coField.getZcBAgency();        BeanUtil.set("zcAgeyCode", zcBAgency == null ? null : zcBAgency.getAgency(), getEditObject());        BeanUtil.set("zcAgeyName", zcBAgency == null ? null : zcBAgency.getAgencyName(), getEditObject());      }    }    this.fireEditSynced();  }  public Object getValue() {    return coField.getZcBAgency();  }  public void setValue(Object value) {    coField.setValueByCode((String) BeanUtil.get(fieldName, value));  }  public void setEditable(boolean enabled) {    coField.setEditable(enabled);  }}