package com.ufgov.zc.client.console.elementrule;import java.awt.BorderLayout;import javax.swing.JPanel;import com.ufgov.zc.client.console.elementrule.event.RuleModelEvent;import com.ufgov.zc.client.console.elementrule.event.RuleModelListener;public abstract class RuleView extends JPanel implements RuleModelListener {  protected RuleEntryModel ruleEntryModel;  public RuleView() {    this.setLayout(new BorderLayout());  }  /**   * @return the model   */  public Object getRuleEntryModel() {    return ruleEntryModel;  }  public void setRuleEntryModel(RuleEntryModel model) {    if (this.ruleEntryModel != null && this.ruleEntryModel != model) {      this.ruleEntryModel.removeRuleModelListener(this);    }    this.ruleEntryModel = model;    initWithRuleEntryModel(model);    this.ruleEntryModel.addRuleModelListener(this);  }  public void ruleChanged(RuleModelEvent e) {    RuleEntryModel model = (RuleEntryModel) e.getSource();    initWithRuleEntryModel(model);  }  public abstract void initWithRuleEntryModel(RuleEntryModel model);}