package com.ufgov.zc.client.datacache;import java.util.Collections;import java.util.HashMap;import java.util.List;import java.util.Map;import com.ufgov.zc.client.common.WorkEnv;import com.ufgov.zc.common.commonbiz.model.BalMode;import com.ufgov.zc.common.system.RequestMeta;import com.ufgov.zc.common.system.dto.ElementConditionDto;public class BalModeDataCache {  private static List dataList;  private static Map dataMap;  private static void getData() {    if (dataList == null) {      refreshData();    }  }  public static synchronized void refreshData() {    int nd = WorkEnv.getInstance().getTransNd();    RequestMeta requestMeta = WorkEnv.getInstance().getRequestMeta();    ElementConditionDto dto = new ElementConditionDto();    dto.setNd(nd);    dataList = Collections.unmodifiableList(Util.baseDataServiceDelegate.getBalMode(dto, requestMeta));    Map tempMap = new HashMap();    for (Object o : dataList) {      BalMode v = (BalMode) o;      tempMap.put(v.getBalModeCode(), v);    }    dataMap = Collections.unmodifiableMap(tempMap);  }  public static List getBalMode() {    getData();    return dataList;  }  public static String getName(String code) {    getData();    BalMode value = (BalMode) dataMap.get(code);    if (value != null) {      return value.getBalModeName();    }    return code;  }}