package com.ufgov.zc.client.datacache;import java.util.Collections;import java.util.HashMap;import java.util.List;import java.util.Map;import com.ufgov.zc.client.common.WorkEnv;import com.ufgov.zc.common.commonbiz.model.MaBank;import com.ufgov.zc.common.system.RequestMeta;public class BankDataCache {  private static List dataList;  private static Map dataMap;  private static void getData() {    if (dataList == null) {      refreshData();    }  }  public static synchronized void refreshData() {    int nd = WorkEnv.getInstance().getTransNd();    RequestMeta requestMeta = WorkEnv.getInstance().getRequestMeta();    dataList = Collections.unmodifiableList(Util.baseDataServiceDelegate.getAllBank(nd, requestMeta));    Map tempMap = new HashMap();    for (Object o : dataList) {      MaBank temp = (MaBank) o;      tempMap.put(temp.getCode(), temp);    }    dataMap = Collections.unmodifiableMap(tempMap);  }  public static List getBank() {    getData();    return dataList;  }  public static MaBank getBean(String code) {    getData();    MaBank value = (MaBank) dataMap.get(code);    return value;  }  public static String getName(String code) {    getData();    MaBank value = (MaBank) dataMap.get(code);    if (value != null) {      return value.getName();    }    return code;  }}