package com.ufgov.zc.client.datacache;import java.util.Collections;import java.util.HashMap;import java.util.List;import java.util.Map;import com.ufgov.zc.client.common.WorkEnv;import com.ufgov.zc.common.commonbiz.model.Manage;import com.ufgov.zc.common.system.RequestMeta;import com.ufgov.zc.common.system.dto.ElementConditionDto;public class ManageForBiXJDataCache {  private static List dataList;  private static Map dataMap;  private static Map tempCodeMap;  private static void getData() {    if (dataList == null) {      refreshData();    }  }  public static synchronized void refreshData() {    int nd = WorkEnv.getInstance().getTransNd();    RequestMeta requestMeta = WorkEnv.getInstance().getRequestMeta();    ElementConditionDto dto = new ElementConditionDto();    dto.setNd(nd);    dataList = Collections.unmodifiableList(Util.baseDataServiceDelegate.getManageForBiXJ(dto, requestMeta));    Map tempMap = new HashMap();    tempCodeMap = new HashMap();    for (Object o : dataList) {      Manage manage = (Manage) o;      tempMap.put(manage.getOid(), manage);      tempCodeMap.put(manage.getCode(), manage);    }    dataMap = Collections.unmodifiableMap(tempMap);  }  public static List getManage() {    getData();    return dataList;  }  public static String getName(String code) {    getData();    Manage value = (Manage) tempCodeMap.get(code);    if (value != null) {      return value.getName();    }    return code;  }  public static Manage getBean(String code) {    getData();    Manage value = (Manage) dataMap.get(code);    return value;  }}