package com.ufgov.zc.client.datacache;import java.util.Collections;import java.util.HashMap;import java.util.List;import java.util.Map;import com.ufgov.zc.client.common.WorkEnv;import com.ufgov.zc.common.commonbiz.model.SendDocType;import com.ufgov.zc.common.system.RequestMeta;public class SendDocTypeDataCache {  private static List dataList;  private static Map dataMap;  private static void getData() {    if (dataList == null) {      refreshData();    }  }  public static synchronized void refreshData() {    int nd = WorkEnv.getInstance().getTransNd();    RequestMeta requestMeta = WorkEnv.getInstance().getRequestMeta();    dataList = Collections.unmodifiableList(Util.baseDataServiceDelegate.getSendDocType(nd, requestMeta));    Map tempMap = new HashMap();    for (Object o : dataList) {      SendDocType value = (SendDocType) o;      tempMap.put(value.getCode(), value);    }    dataMap = Collections.unmodifiableMap(tempMap);  }  public static List getSendDocType() {    getData();    return dataList;  }  public static String getName(String code) {    getData();    SendDocType value = (SendDocType) dataMap.get(code);    if (value != null) {      return value.getName();    }    return code;  }}