package com.ufgov.zc.server.am.dao.ibatis;import java.util.List;import org.springframework.orm.ibatis.support.SqlMapClientDaoSupport;import com.ufgov.zc.common.system.RequestMeta;import com.ufgov.zc.common.system.constants.NumLimConstants;import com.ufgov.zc.common.system.dto.ElementConditionDto;import com.ufgov.zc.server.am.dao.IAmSpecialAccountDao;import com.ufgov.zc.server.system.util.NumLimUtil;import com.ufgov.zc.server.system.util.OrderColumnsUtil;public class AmSpecialAccountDao extends SqlMapClientDaoSupport implements IAmSpecialAccountDao {  public List getSpecialAccount(ElementConditionDto dto, RequestMeta requestMeta) {    dto.setNumLimitStr(NumLimUtil.getInstance().getNumLimCondByCoType(dto.getNumLimCompoId(),    NumLimConstants.FWATCH));    dto.setOrderColumns(OrderColumnsUtil.getInstance().getOrderColumns(dto.getOrderCompoId()));    return this.getSqlMapClientTemplate().queryForList("AmSpecialAccount.getSpecialAccount", dto);  }}